package week1.day1;

public class Oddnumber {

	public static void main(String[] args) {// entry ponit of your code it will not return anything
		// TODO Auto-generated method stub
		for(int i=1;i<20;i++) {
			if(i%2!=0)
			{
				System.out.println("This number is odd :"+i);
			}else {
				System.out.println("This number is even :"+i);
			}
			
		}
			
	}

}
